lessons.codeExample:
// Variable declarations
let name = 'John';
const age = 25;
var city = 'New York';

// Block scoping with let and const
if (true) {
  let blockScoped = 'only available here';
  const alsoBlockScoped = 'same here';
}

// Function scoping with var
function example() {
  var functionScoped = 'available throughout function';
}

// Primitive types
let name = 'Alice';        // string
let age = 30;              // number
let isActive = true;       // boolean
let data = null;           // null
let notDefined;            // undefined
let id = Symbol('id');     // symbol

// Object types
let person = {             // object
  name: 'Bob',
  age: 25
};
let numbers = [1, 2, 3];   // array (special object)
let greet = function() {   // function (special object)
  return 'Hello!';
};

// Function declaration
function greet(name) {
  return `Hello, ${name}!`;
}

// Function expression
const sayHi = function(name) {
  return `Hi, ${name}!`;
};

// Arrow function (ES6)
const sayHey = (name) => `Hey, ${name}!`;

// Function with default parameters
function introduce(name = 'Anonymous', age = 0) {
  return `I'm ${name} and I'm ${age} years old.`;
}

// Using functions
console.log(greet('Alice'));           // Hello, Alice!
console.log(introduce('Bob', 25));     // I'm Bob and I'm 25 years old.

// Creating arrays
const fruits = ['apple', 'banana', 'orange'];
const numbers = [1, 2, 3, 4, 5];
const mixed = ['text', 42, true, null];

// Array methods
fruits.push('grape');           // Add to end: ['apple', 'banana', 'orange', 'grape']
fruits.pop();                   // Remove from end: ['apple', 'banana', 'orange']
fruits.unshift('mango');        // Add to beginning: ['mango', 'apple', 'banana', 'orange']
fruits.shift();                 // Remove from beginning: ['apple', 'banana', 'orange']

// Iteration and transformation
fruits.forEach(fruit => console.log(fruit));
const upperFruits = fruits.map(fruit => fruit.toUpperCase());
const longFruits = fruits.filter(fruit => fruit.length > 5);

// Object literal
const person = {
  name: 'Alice',
  age: 30,
  city: 'New York',
  greet() {
    return `Hi, I'm ${this.name} from ${this.city}`;
  }
};

// Accessing properties
console.log(person.name);           // Dot notation
console.log(person['age']);         // Bracket notation

// Adding/modifying properties
person.email = 'alice@email.com';
person.age = 31;

// Object destructuring
const { name, age } = person;

// Object methods
const keys = Object.keys(person);       // ['name', 'age', 'city', 'email', 'greet']
const values = Object.values(person);   // Values array
const entries = Object.entries(person); // [key, value] pairs

