// JavaScript's first 'Hello World' (1995)
document.write('Hello, World!');

// Modern JavaScript (2024)
console.log('Hello, World!');

// Fun fact: This simple evolution shows 29 years of progress!

// Java (completely different language)
public class HelloWorld {
    public static void main(String[] args) {
        System.out.println("Hello, World!");
    }
}

// JavaScript (our beloved web language)
function sayHello() {
    console.log('Hello, World!');
}
sayHello();

// As you can see, they're as different as pizza and pineapple!

// Frontend (Browser)
document.getElementById('app').innerHTML = 'Hello Web!';

// Backend (Node.js)
const express = require('express');
const app = express();
app.get('/', (req, res) => res.send('Hello Server!'));

