// Creating variables
let playerName = 'Alex';
const maxScore = 100;

// Changing values
playerName = 'Sam';  // ‚úÖ Works with let
// maxScore = 200;   // ‚ùå Error with const

console.log(playerName); // Sam
console.log(maxScore);   // 100

// Basic numbers
const price = 25;
const quantity = 3;

// Math operations
const total = price * quantity;     // 75
const discount = total - 10;        // 65
const average = total / 2;          // 37.5
const rounded = Math.round(average); // 38

console.log(`Total: $${total}`);
console.log(`After discount: $${discount}`);
console.log(`Rounded average: ${rounded}`);

// Useful number methods
const pi = 3.14159;
console.log(pi.toFixed(2)); // 3.14

// Creating strings
const firstName = 'John';
const lastName = 'Doe';

// Combining strings
const fullName = firstName + ' ' + lastName;
const greeting = `Hello, ${firstName}!`; // Template literal

// String methods
console.log(fullName.length);        // 8
console.log(fullName.toUpperCase());  // JOHN DOE
console.log(fullName.toLowerCase());  // john doe
console.log(firstName.charAt(0));     // J

// Checking if text contains something
const message = 'Welcome to JavaScript';
console.log(message.includes('Java')); // true
console.log(message.startsWith('Welcome')); // true
